import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginPage from "./pages/LoginPage";
import Dashboard from "./pages/Dashboard";
import ProjetPage from "./pages/ProjetPage";
import GroupePage from "./pages/GroupePage";
import RapportPage from "./pages/RapportPage";
import MessagesPage from "./pages/MessagesPage";

function App() {
  const isAuthenticated = !!localStorage.getItem("token");
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route
          path="/"
          element={isAuthenticated ? <Dashboard /> : <Navigate to="/login" />}
        />
        <Route
          path="/projet"
          element={isAuthenticated ? <ProjetPage /> : <Navigate to="/login" />}
        />
        <Route
          path="/groupes"
          element={isAuthenticated ? <GroupePage /> : <Navigate to="/login" />}
        />
        <Route
          path="/rapports"
          element={isAuthenticated ? <RapportPage /> : <Navigate to="/login" />}
        />
        <Route
          path="/messages"
          element={isAuthenticated ? <MessagesPage /> : <Navigate to="/login" />}
        />
      </Routes>
    </Router>
  );
}

export default App;
