import React, { useEffect, useState } from "react";
import api from "../api/axios";

function MessagesPage() {
  const [projet, setProjet] = useState(null);
  const [messages, setMessages] = useState([]);
  const [contenu, setContenu] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadProjetAndMessages();
  }, []);

  const loadProjetAndMessages = async () => {
    try {
      setLoading(true);
      setError("");
      
      console.log("üîç Chargement du projet...");
      // Charger le projet
      const projetResponse = await api.get("/projets/mon-projet");
      const projetData = projetResponse.data;
      console.log("üìã Projet re√ßu:", projetData);
      
      if (projetData && projetData.id) {
        setProjet(projetData);
        console.log("‚úÖ Projet d√©fini dans l'√©tat:", projetData.id);
        
        // Charger les messages
        console.log("üì® Chargement des messages pour le projet:", projetData.id);
        const messagesResponse = await api.get(`/messages/projet/${projetData.id}`);
        const messagesData = Array.isArray(messagesResponse.data) ? messagesResponse.data : [];
        console.log("üì® Messages re√ßus:", messagesData.length);
        setMessages(messagesData);
      } else {
        console.log("‚ùå Aucun projet trouv√© ou projet invalide");
        setProjet(null);
        setMessages([]);
      }
    } catch (err) {
      console.error("‚ùå Erreur lors du chargement:", err);
      setError("Erreur lors du chargement du projet ou des messages");
      setProjet(null);
      setMessages([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (error) {
    return <div style={{color:'red'}}>{error}</div>;
  }

  if (!projet || !projet.id) {
    return <div style={{color:'red'}}>Vous n'avez pas de projet. Cr√©ez un projet pour utiliser la messagerie.</div>;
  }

  const handleSend = async (e) => {
    e.preventDefault();
    if (!contenu || !projet) return;
    
    console.log("üöÄ Envoi du message pour le projet:", projet.id);
    console.log("üìù Contenu du message:", contenu);
    
    try {
      // Envoyer le message
      console.log("üì§ Envoi du message √† l'API...");
      const sendResponse = await api.post(`/messages/ajouter/${projet.id}`, { contenu });
      console.log("‚úÖ Message envoy√© avec succ√®s:", sendResponse.data);
      setContenu("");
      
      // Recharger les messages
      console.log("üîÑ Rechargement des messages...");
      const messagesResponse = await api.get(`/messages/projet/${projet.id}`);
      const messagesData = Array.isArray(messagesResponse.data) ? messagesResponse.data : [];
      console.log("üì® Messages mis √† jour:", messagesData.length);
      setMessages(messagesData);
      
      // V√©rifier que le projet existe toujours
      console.log("üîç V√©rification du projet apr√®s envoi...");
      const projetCheckResponse = await api.get("/projets/mon-projet");
      const projetCheckData = projetCheckResponse.data;
      console.log("üìã Projet apr√®s envoi:", projetCheckData);
      
      if (projetCheckData && projetCheckData.id) {
        console.log("‚úÖ Projet toujours valide");
        setProjet(projetCheckData);
      } else {
        console.log("‚ùå Projet perdu apr√®s envoi du message!");
        setProjet(null);
      }
    } catch (err) {
      console.error("‚ùå Erreur lors de l'envoi du message:", err);
      console.error("‚ùå D√©tails de l'erreur:", err.response?.data);
      alert("Erreur lors de l'envoi du message. Veuillez r√©essayer.");
    }
  };

  return (
    <div>
      <h2>Messagerie du projet</h2>
      <div style={{ maxHeight: 300, overflowY: "auto", border: "1px solid #ccc", padding: 10 }}>
        {messages.map((m) => (
          <div key={m.id}>
            <b>{m.auteur && m.auteur.nom} :</b> {m.contenu} <span style={{ fontSize: 10, color: '#888' }}>{new Date(m.date).toLocaleString()}</span>
          </div>
        ))}
      </div>
      <form onSubmit={handleSend}>
        <input
          value={contenu}
          onChange={(e) => setContenu(e.target.value)}
          placeholder="Votre message"
          required
        />
        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
}

export default MessagesPage;
