import React, { useEffect, useState } from "react";
import api from "../api/axios";

function MessagesPage() {
  const [projet, setProjet] = useState(null);
  const [messages, setMessages] = useState([]);
  const [contenu, setContenu] = useState("");

  useEffect(() => {
    api.get("/projets/mon-projet").then((res) => {
      setProjet(res.data);
      if (res.data && res.data.id) {
        api.get(`/messages/projet/${res.data.id}`).then((r) => setMessages(Array.isArray(r.data) ? r.data : []));
      } else {
        setMessages([]);
      }
    });
  }, []);

  if (!projet || !projet.id) {
    return <div style={{color:'red'}}>Vous n'avez pas de projet. Cr√©ez un projet pour utiliser la messagerie.</div>;
  }

  const handleSend = async (e) => {
    e.preventDefault();
    if (!contenu || !projet) return;
    try {
      await api.post(`/messages/ajouter/${projet.id}`, { contenu });
      setContenu("");
      api.get(`/messages/projet/${projet.id}`).then((r) => setMessages(Array.isArray(r.data) ? r.data : []));
    } catch (err) {
      alert("Erreur lors de l'envoi du message");
    }
  };

  return (
    <div>
      <h2>Messagerie du projet</h2>
      <div style={{ maxHeight: 300, overflowY: "auto", border: "1px solid #ccc", padding: 10 }}>
        {messages.map((m) => (
          <div key={m.id}>
            <b>{m.auteur && m.auteur.nom} :</b> {m.contenu} <span style={{ fontSize: 10, color: '#888' }}>{new Date(m.date).toLocaleString()}</span>
          </div>
        ))}
      </div>
      <form onSubmit={handleSend}>
        <input
          value={contenu}
          onChange={(e) => setContenu(e.target.value)}
          placeholder="Votre message"
          required
        />
        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
}

export default MessagesPage;
